#pragma config(UART_Usage, UART1, uartVEXLCD, baudRate19200, IOPins, None, None)
//*!!Code automatically generated by 'ROBOTC' configuration wizard               !!*//

void RightSide10()
{
	displayLCDCenteredString(0,"Right Side 10 Auto"); // 1st line on LCD
}


void RightSide20()
{
	displayLCDCenteredString(0,"Right Side 20 Auto"); // 1st line on LCD
}

void LeftSide10()
{
	displayLCDCenteredString(0,"left Side 10 Auto"); // 1st line on LCD
}
void LeftSide20()
{
	displayLCDCenteredString(0,"left Side 20 Auto"); // 1st line on LCD
}
void Skills()
{
	displayLCDCenteredString(0,"Skills"); // 1st line on LCD
}
void noAuto()
{
	displayLCDCenteredString(0,"dskfhwfwi"); // 1st line on LCD
}

const short leftButton = 1; // setup left button LCD
const short centerButton = 2; // setup center button LCD
const short rightButton = 4; // setup right button LCD


void showAutonomous(int sel)
{

	displayLCDCenteredString(1, "<     Enter    >");  // LCD's second line displays left right and "enter" buttons

	switch(sel)
	{
	case 0:
		displayLCDCenteredString(0, "Left Side 10");
		break;
	case 1:
		displayLCDCenteredString(0, "Right Side 10");
		break;
	case 2:
		displayLCDCenteredString(0, "Left Side 20");
		break;
	case 3:
		displayLCDCenteredString(0, "Right Side 20");
		break;
	case 4:
		displayLCDCenteredString(0, "Skills Auto");
		break;
	case 5:
		displayLCDCenteredString(0, "No Auto");
		break;
	}
}

int previousAutonomous(int c)
{
	int p;
	if (c == 0) {
		p = 5;
	}
	else {
		p = c - 1;
	}
	return p;
}

int nextAutonomous(int c)
{
	int n;
	if (c == 5) {
		n = 0;
	}
	else {
		n = c + 1;
	}
	return n;
}

void waitForRelease()
{
	while(nLCDButtons != 0) {}
	wait1Msec(150);
}


int waitForPress()
{
	while(nLCDButtons == 0)
	{
	}
	return nLCDButtons;
}

int chooseAutonomous()
{
	int sel = 0;
	int key;
	while (true) {
		showAutonomous(sel);
		key = waitForPress();
		waitForRelease();
		switch(key)
		{
		case leftButton:
			sel = previousAutonomous(sel);
			break;
		case centerButton:
			return sel;
			break;
		case rightButton:
			sel = nextAutonomous(sel);
			break;

		}
	}
}

void selectAutonomous(int sel)
{
	switch(sel)
	{
	case 0:
		LeftSide10();
		break;
	case 1:
		RightSide10();
		break;
	case 2:
		LeftSide20();
		break;
	case 3:
		RightSide20();
		break;
	case 4:
		Skills();
		break;
	case 5:
		noAuto();
		break;
	}
	wait1Msec(2000);

}


task main()
{
	int sel;
	clearLCDLine(0);
	clearLCDLine(1);
	while(true)
	{
		sel = chooseAutonomous();
		selectAutonomous(sel);
	}

}
